import React, { useEffect, useState } from 'react';
import { Plus, List, RefreshCw, LogOut, LogIn, Home, ShieldCheck, Search } from 'lucide-react';
import { QRCodeSVG } from 'qrcode.react';
import './App.css';

const API_BASE =
    (process.env.REACT_APP_API_BASE_URL && process.env.REACT_APP_API_BASE_URL.trim()) ||
    (process.env.NEXT_PUBLIC_API_BASE_URL && process.env.NEXT_PUBLIC_API_BASE_URL.trim()) ||
    'https://ledgrx.duckdns.org';

const buildUrl = (path: string) => `${API_BASE.replace(/\/$/, '')}${path}`;

type Medication = {
    serialNumber: string;
    medicationName: string;
    gtin: string;
    batchNumber: string;
    expiryDate: string;
    productionCompany: string;
    distributionCompany: string;
    qrHash?: string;
};

type AuthUser = {
    username: string;
    password: string;
};

const App: React.FC = () => {
    const [route, setRoute] = useState(window.location.pathname || '/');
    const [activeTab, setActiveTab] = useState<'add' | 'view'>('add');
    const [medications, setMedications] = useState<Medication[]>([]);
    const [formData, setFormData] = useState<Medication>({
        serialNumber: '',
        medicationName: '',
        gtin: '',
        batchNumber: '',
        expiryDate: '',
        productionCompany: '',
        distributionCompany: '',
        qrHash: ''
    });
    const [showQRModal, setShowQRModal] = useState(false);
    const [selectedQRHash, setSelectedQRHash] = useState('');
    const [authToken, setAuthToken] = useState<string | null>(null);
    const [authError, setAuthError] = useState('');
    const [loginForm, setLoginForm] = useState<AuthUser>({ username: '', password: '' });
    const [statusMessage, setStatusMessage] = useState('');
    const [lookupSerial, setLookupSerial] = useState('');
    const [lookupResult, setLookupResult] = useState<Medication | null>(null);
    const [lookupError, setLookupError] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        const stored = localStorage.getItem('authToken');
        if (stored) {
            setAuthToken(stored);
        }
    }, []);

    useEffect(() => {
        const onPopState = () => setRoute(window.location.pathname || '/');
        window.addEventListener('popstate', onPopState);
        return () => window.removeEventListener('popstate', onPopState);
    }, []);

    const navigate = (path: string) => {
        if (path === route) return;
        window.history.pushState({}, '', path);
        setRoute(path);
    };

    const authFetch = (path: string, options: RequestInit = {}) => {
        const headers = new Headers(options.headers || {});
        if (authToken) {
            headers.set('Authorization', `Bearer ${authToken}`);
        }
        if (!headers.has('Content-Type') && options.body) {
            headers.set('Content-Type', 'application/json');
        }
        return fetch(buildUrl(path), { ...options, headers });
    };

    const fetchMedications = async () => {
        setStatusMessage('');
        setIsLoading(true);
        try {
            const response = await authFetch('/api/medications');
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                throw new Error(errorData.error || 'Failed to fetch medications.');
            }
            const data = await response.json();
            setMedications(data);
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setStatusMessage(message || 'Unable to load medications.');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        if (route === '/app' && activeTab === 'view') {
            fetchMedications();
        }
    }, [route, activeTab]);

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setStatusMessage('');
        if (
            !formData.serialNumber ||
            !formData.medicationName ||
            !formData.gtin ||
            !formData.batchNumber ||
            !formData.expiryDate ||
            !formData.productionCompany ||
            !formData.distributionCompany
        ) {
            setStatusMessage('All fields are required.');
            return;
        }
        try {
            const response = await authFetch('/api/medications', {
                method: 'POST',
                body: JSON.stringify({
                    serialNumber: formData.serialNumber,
                    medicationName: formData.medicationName,
                    gtin: formData.gtin,
                    batchNumber: formData.batchNumber,
                    expiryDate: formData.expiryDate,
                    productionCompany: formData.productionCompany,
                    distributionCompany: formData.distributionCompany
                })
            });
            if (response.ok) {
                setStatusMessage('Medication added successfully!');
                setFormData({
                    serialNumber: '',
                    medicationName: '',
                    gtin: '',
                    batchNumber: '',
                    expiryDate: '',
                    productionCompany: '',
                    distributionCompany: '',
                    qrHash: ''
                });
            } else {
                const errorData = await response.json().catch(() => ({}));
                setStatusMessage(errorData.error || 'Upload failed.');
            }
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setStatusMessage(message || 'An error occurred while adding the medication.');
        }
    };

    const handleShowQR = (qrHash: string) => {
        if (showQRModal && selectedQRHash === qrHash) {
            setShowQRModal(false);
            return;
        }
        setSelectedQRHash(qrHash);
        setShowQRModal(true);
    };

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setAuthError('');
        if (!loginForm.username || !loginForm.password) {
            setAuthError('Enter your username and password.');
            return;
        }
        try {
            const response = await fetch(buildUrl('/api/auth/login'), {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginForm)
            });
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                setAuthError(errorData.error || 'Login failed.');
                return;
            }
            const data = await response.json();
            if (!data.token) {
                setAuthError('Login failed.');
                return;
            }
            localStorage.setItem('authToken', data.token);
            setAuthToken(data.token);
            setLoginForm({ username: '', password: '' });
            navigate('/app');
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setAuthError(message || `Login failed. Check API at ${API_BASE}.`);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('authToken');
        setAuthToken(null);
        navigate('/login');
    };

    const handleLookup = async () => {
        setLookupError('');
        setLookupResult(null);
        if (!lookupSerial.trim()) {
            setLookupError('Enter a serial number to search.');
            return;
        }
        try {
            const response = await authFetch(`/api/medications/${encodeURIComponent(lookupSerial.trim())}`);
            if (response.status === 404) {
                const errorData = await response.json().catch(() => ({}));
                setLookupError(errorData.error || 'Medication not found.');
                return;
            }
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                setLookupError(errorData.error || 'Lookup failed.');
                return;
            }
            const data = await response.json();
            setLookupResult(data);
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setLookupError(message || 'Lookup failed.');
        }
    };

    const handleHealthCheck = async () => {
        try {
            const response = await fetch(buildUrl('/api/health'));
            if (!response.ok) {
                throw new Error('Backend not reachable.');
            }
            setStatusMessage('Backend online.');
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setStatusMessage(message || 'Backend offline.');
        }
    };

    const filteredMedications = medications;

    const renderHome = () => (
        <div className="page page--hero">
            <div className="hero-card">
                <h1>LedgerRx</h1>
                <p>
                    LedgerRx provides secure, immutable tracking for pharmaceuticals. Record medication provenance,
                    verify production sources, and track distribution on your private Hyperledger Fabric network.
                </p>
                <div className="cta-row">
                    <button className="primary" onClick={() => navigate('/login')}>
                        <LogIn size={16} />
                        Sign in
                    </button>
                    <button className="ghost" onClick={() => navigate('/app')}>
                        <ShieldCheck size={16} />
                        Open dashboard
                    </button>
                </div>
                <div className="hero-meta">
                    <span>Private network</span>
                    <span>Verified supply chain</span>
                    <span>QR traceability</span>
                </div>
            </div>
        </div>
    );

    const renderLogin = () => (
        <div className="page page--center">
            <form className="card auth-card" onSubmit={handleLogin}>
                <div>
                    <h1>Welcome back</h1>
                    <p>Sign in to continue managing medication records.</p>
                </div>
                <label>
                    Username
                    <input
                        type="text"
                        value={loginForm.username}
                        onChange={(e) => setLoginForm({ ...loginForm, username: e.target.value })}
                        required
                    />
                </label>
                <label>
                    Password
                    <input
                        type="password"
                        value={loginForm.password}
                        onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
                        required
                    />
                </label>
                {authError && <div className="error">{authError}</div>}
                <button type="submit" className="primary">
                    Sign in
                </button>
                <button type="button" className="ghost" onClick={() => navigate('/')}>
                    <Home size={16} />
                    Back to home
                </button>
            </form>
        </div>
    );

    const renderDashboard = () => (
        <div className="page">
            <h1>Medication Dashboard</h1>
            <div className="toolbar">
                <button onClick={() => setActiveTab('add')} className={activeTab === 'add' ? 'active' : ''}>
                    <Plus size={16} />
                    Add Medication
                </button>
                <button onClick={() => setActiveTab('view')} className={activeTab === 'view' ? 'active' : ''}>
                    <List size={16} />
                    View All
                </button>
                <button onClick={handleHealthCheck}>
                    <ShieldCheck size={16} />
                    Health
                </button>
            </div>
            {statusMessage && <div className="status">{statusMessage}</div>}
            {activeTab === 'add' && (
                <form onSubmit={handleSubmit} className="card form-grid">
                    <input
                        type="text"
                        name="serialNumber"
                        placeholder="Serial Number (UID)"
                        value={formData.serialNumber}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="text"
                        name="medicationName"
                        placeholder="Medication Name"
                        value={formData.medicationName}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="text"
                        name="gtin"
                        placeholder="GTIN"
                        value={formData.gtin}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="text"
                        name="batchNumber"
                        placeholder="Batch Number"
                        value={formData.batchNumber}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="date"
                        name="expiryDate"
                        placeholder="Expiry Date"
                        value={formData.expiryDate}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="text"
                        name="productionCompany"
                        placeholder="Production Company"
                        value={formData.productionCompany}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="text"
                        name="distributionCompany"
                        placeholder="Distribution Company"
                        value={formData.distributionCompany}
                        onChange={handleInputChange}
                        required
                    />
                    <button type="submit" className="primary">
                        Add to Blockchain
                    </button>
                </form>
            )}
            {activeTab === 'view' && (
                <div>
                    <div className="toolbar">
                        <button onClick={fetchMedications}>
                            <RefreshCw size={16} />
                            {isLoading ? 'Loading...' : 'Refresh'}
                        </button>
                    </div>
                    <div className="lookup card">
                        <h3>Lookup by serial number</h3>
                        <div className="lookup-row">
                            <input
                                type="text"
                                placeholder="Serial number"
                                value={lookupSerial}
                                onChange={(e) => setLookupSerial(e.target.value)}
                            />
                            <button className="primary" type="button" onClick={handleLookup}>
                                <Search size={16} />
                                Search
                            </button>
                        </div>
                        {lookupError && <div className="error">{lookupError}</div>}
                        {lookupResult && (
                            <div className="lookup-result">
                                <div>
                                    <strong>{lookupResult.medicationName}</strong>
                                    <span>{lookupResult.serialNumber}</span>
                                </div>
                                <div>GTIN: {lookupResult.gtin}</div>
                                <div>Batch: {lookupResult.batchNumber}</div>
                                <div>Expiry: {lookupResult.expiryDate}</div>
                                <div>Production: {lookupResult.productionCompany}</div>
                                <div>Distribution: {lookupResult.distributionCompany}</div>
                            </div>
                        )}
                    </div>
                    <div className="card table-card">
                        <table>
                            <thead>
                                <tr>
                                    <th>Serial</th>
                                    <th>Name</th>
                                    <th>GTIN</th>
                                    <th>Batch</th>
                                    <th>Expiry</th>
                                    <th>Production</th>
                                    <th>Distribution</th>
                                    <th>QR</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredMedications.map((med) => (
                                    <tr key={med.serialNumber}>
                                        <td>{med.serialNumber}</td>
                                        <td>{med.medicationName}</td>
                                        <td>{med.gtin}</td>
                                        <td>{med.batchNumber}</td>
                                        <td>{med.expiryDate}</td>
                                        <td>{med.productionCompany}</td>
                                        <td>{med.distributionCompany}</td>
                                        <td>
                                            {med.qrHash ? (
                                                <button onClick={() => handleShowQR(med.qrHash || '')}>Show QR</button>
                                            ) : (
                                                'â€”'
                                            )}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}
        </div>
    );

    const showDashboard = route === '/app';
    const showLogin = route === '/login' || (!authToken && route === '/app');
    const showHome = !showDashboard && !showLogin;

    return (
        <div className="app-shell">
            <header className="topbar">
                <div className="logo" onClick={() => navigate('/')}>
                    LedgerRx
                </div>
                <nav>
                    <button onClick={() => navigate('/')}>
                        <Home size={16} />
                        Home
                    </button>
                    <button onClick={() => navigate('/app')}>
                        <ShieldCheck size={16} />
                        Dashboard
                    </button>
                    {!authToken && (
                        <button onClick={() => navigate('/login')}>
                            <LogIn size={16} />
                            Sign in
                        </button>
                    )}
                    {authToken && (
                        <button onClick={handleLogout}>
                            <LogOut size={16} />
                            Sign out
                        </button>
                    )}
                </nav>
            </header>

            <main>
                {showHome && renderHome()}
                {showLogin && renderLogin()}
                {showDashboard && authToken && renderDashboard()}
            </main>

            {showQRModal && (
                <div className="modal" onClick={() => setShowQRModal(false)}>
                    <div className="modal-card" onClick={(e) => e.stopPropagation()}>
                        <QRCodeSVG value={selectedQRHash} size={256} />
                        <p>{selectedQRHash}</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default App;
