import React, { useEffect, useState } from 'react';
import { Plus, List, RefreshCw, LogIn, ShieldCheck, Home } from 'lucide-react';
import { QRCodeSVG } from 'qrcode.react';
import './App.css';

const resolveApiBase = () => {
    const explicit = process.env.REACT_APP_API_BASE_URL || process.env.NEXT_PUBLIC_API_BASE_URL;
    if (explicit && explicit.trim()) {
        return explicit.trim();
    }
    if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
        return 'http://localhost:3001';
    }
    return 'https://ledgrx.duckdns.org';
};

const API_BASE = resolveApiBase();
const buildUrl = (path: string) => `${API_BASE.replace(/\/$/, '')}${path}`;

type Medication = {
    serialNumber: string;
    medicationName: string;
    gtin: string;
    batchNumber: string;
    expiryDate: string;
    productionCompany: string;
    distributionCompany: string;
    qrHash?: string;
};

const App: React.FC = () => {
    const [route, setRoute] = useState(window.location.pathname || '/');
    const [activeTab, setActiveTab] = useState<'add' | 'view'>('add');
    const [medications, setMedications] = useState<Medication[]>([]);
    const [authToken, setAuthToken] = useState<string | null>(null);
    const [authError, setAuthError] = useState('');
    const [statusMessage, setStatusMessage] = useState('');
    const [showQRModal, setShowQRModal] = useState(false);
    const [selectedQRHash, setSelectedQRHash] = useState('');
    const [formData, setFormData] = useState<Medication>({
        serialNumber: '',
        medicationName: '',
        gtin: '',
        batchNumber: '',
        expiryDate: '',
        productionCompany: '',
        distributionCompany: '',
        qrHash: ''
    });
    const [loginForm, setLoginForm] = useState({ username: '', password: '' });

    useEffect(() => {
        const stored = localStorage.getItem('authToken');
        if (stored) {
            setAuthToken(stored);
        }
    }, []);

    useEffect(() => {
        const onPopState = () => setRoute(window.location.pathname || '/');
        window.addEventListener('popstate', onPopState);
        return () => window.removeEventListener('popstate', onPopState);
    }, []);

    const navigate = (path: string) => {
        if (path === route) return;
        window.history.pushState({}, '', path);
        setRoute(path);
    };

    const authFetch = (path: string, options: RequestInit = {}) => {
        const headers = new Headers(options.headers || {});
        if (authToken) {
            headers.set('Authorization', `Bearer ${authToken}`);
        }
        if (!headers.has('Content-Type') && options.body) {
            headers.set('Content-Type', 'application/json');
        }
        return fetch(buildUrl(path), { ...options, headers });
    };

    const fetchMedications = async () => {
        setStatusMessage('');
        try {
            const response = await authFetch('/api/medications');
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                throw new Error(errorData.error || 'Failed to fetch medications.');
            }
            const data = await response.json();
            setMedications(data);
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setStatusMessage(message || 'Unable to load medications.');
        }
    };

    useEffect(() => {
        if (route === '/app' && activeTab === 'view') {
            fetchMedications();
        }
    }, [route, activeTab]);

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setStatusMessage('');
        if (
            !formData.serialNumber ||
            !formData.medicationName ||
            !formData.gtin ||
            !formData.batchNumber ||
            !formData.expiryDate ||
            !formData.productionCompany ||
            !formData.distributionCompany
        ) {
            setStatusMessage('All fields are required.');
            return;
        }
        try {
            const response = await authFetch('/api/medications', {
                method: 'POST',
                body: JSON.stringify({
                    serialNumber: formData.serialNumber,
                    medicationName: formData.medicationName,
                    gtin: formData.gtin,
                    batchNumber: formData.batchNumber,
                    expiryDate: formData.expiryDate,
                    productionCompany: formData.productionCompany,
                    distributionCompany: formData.distributionCompany
                })
            });
            if (response.ok) {
                setStatusMessage('Medication added successfully!');
                setFormData({
                    serialNumber: '',
                    medicationName: '',
                    gtin: '',
                    batchNumber: '',
                    expiryDate: '',
                    productionCompany: '',
                    distributionCompany: '',
                    qrHash: ''
                });
            } else {
                const errorData = await response.json().catch(() => ({}));
                setStatusMessage(errorData.error || 'Upload failed.');
            }
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setStatusMessage(message || 'An error occurred while adding the medication.');
        }
    };

    const handleShowQR = (qrHash: string) => {
        if (showQRModal && selectedQRHash === qrHash) {
            setShowQRModal(false);
            return;
        }
        setSelectedQRHash(qrHash);
        setShowQRModal(true);
    };

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setAuthError('');
        if (!loginForm.username || !loginForm.password) {
            setAuthError('Enter your username and password.');
            return;
        }
        try {
            const response = await fetch(buildUrl('/api/auth/login'), {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginForm)
            });
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                setAuthError(errorData.error || 'Login failed.');
                return;
            }
            const data = await response.json();
            if (!data.token) {
                setAuthError('Login failed.');
                return;
            }
            localStorage.setItem('authToken', data.token);
            setAuthToken(data.token);
            setLoginForm({ username: '', password: '' });
            navigate('/app');
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setAuthError(message || `Login failed. Check API at ${API_BASE}.`);
        }
    };

    const containerStyle: React.CSSProperties = {
        minHeight: '100vh',
        backgroundColor: '#f3f4f6',
        padding: '2rem 0'
    };

    const innerContainerStyle: React.CSSProperties = {
        maxWidth: '64rem',
        margin: '0 auto',
        padding: '2rem'
    };

    const cardStyle: React.CSSProperties = {
        backgroundColor: '#fff',
        borderRadius: '0.5rem',
        boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)'
    };

    const tabContainerStyle: React.CSSProperties = {
        display: 'flex',
        borderBottom: '1px solid #e5e7eb'
    };

    const tabButtonStyle: React.CSSProperties = {
        flex: 1,
        padding: '1rem 1.5rem',
        textAlign: 'center',
        fontWeight: 600,
        cursor: 'pointer',
        backgroundColor: 'transparent',
        border: 'none'
    };

    const activeTabButtonStyle: React.CSSProperties = {
        color: '#2563eb',
        borderBottom: '2px solid #2563eb'
    };

    const inactiveTabButtonStyle: React.CSSProperties = {
        color: '#6b7280'
    };

    const formGridStyle: React.CSSProperties = {
        display: 'grid',
        gridTemplateColumns: '1fr',
        gap: '1.5rem'
    };

    const inputStyle: React.CSSProperties = {
        padding: '0.75rem',
        border: '1px solid #d1d5db',
        borderRadius: '0.375rem'
    };

    const submitButtonStyle: React.CSSProperties = {
        width: '100%',
        marginTop: '1.5rem',
        backgroundColor: '#2563eb',
        color: '#fff',
        padding: '0.75rem 0',
        borderRadius: '0.375rem',
        fontWeight: 600,
        cursor: 'pointer',
        border: 'none'
    };

    const secondaryButtonStyle: React.CSSProperties = {
        backgroundColor: '#e5e7eb',
        color: '#4b5563',
        padding: '0.75rem 1rem',
        borderRadius: '0.375rem',
        fontWeight: 600,
        cursor: 'pointer',
        border: 'none',
        display: 'inline-flex',
        alignItems: 'center',
        gap: '0.5rem'
    };

    const refreshButtonStyle: React.CSSProperties = {
        marginBottom: '1rem',
        backgroundColor: '#e5e7eb',
        color: '#4b5563',
        padding: '0.5rem 1rem',
        borderRadius: '0.375rem',
        fontWeight: 600,
        cursor: 'pointer',
        border: 'none',
        display: 'inline-flex',
        alignItems: 'center',
        gap: '0.5rem'
    };

    const tableContainerStyle: React.CSSProperties = {
        overflowX: 'auto'
    };

    const tableStyle: React.CSSProperties = {
        minWidth: '100%',
        backgroundColor: '#fff'
    };

    const tableHeaderStyle: React.CSSProperties = {
        backgroundColor: '#f9fafb'
    };

    const thStyle: React.CSSProperties = {
        padding: '0.75rem 1.5rem',
        textAlign: 'left',
        fontSize: '0.75rem',
        fontWeight: 500,
        color: '#6b7280',
        textTransform: 'uppercase' as const,
        letterSpacing: '0.05em'
    };

    const tdStyle: React.CSSProperties = {
        padding: '1rem 1.5rem',
        whiteSpace: 'nowrap' as const
    };

    const showDashboard = route === '/app';
    const showLogin = route === '/login' || (!authToken && route === '/app');
    const showHome = !showDashboard && !showLogin;

    return (
        <div style={containerStyle}>
            <div style={innerContainerStyle}>
                {showHome && (
                    <>
                        <h1 style={{ fontSize: '1.875rem', lineHeight: '2.25rem', fontWeight: 700, textAlign: 'center', marginBottom: '2rem' }}>
                            Pharma Blockchain MVP
                        </h1>
                        <div style={cardStyle}>
                            <div style={{ padding: '1.5rem', textAlign: 'center', display: 'grid', gap: '1rem' }}>
                                <p>
                                    LedgerRx provides secure, immutable tracking for pharmaceuticals. Record medication provenance, verify
                                    production sources, and track distribution on your private Hyperledger Fabric network.
                                </p>
                                <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', flexWrap: 'wrap' }}>
                                    <button style={submitButtonStyle} onClick={() => navigate('/login')}>
                                        <LogIn style={{ marginRight: '0.5rem' }} size={16} />
                                        Sign in
                                    </button>
                                    <button style={secondaryButtonStyle} onClick={() => navigate('/app')}>
                                        <ShieldCheck size={16} />
                                        Open dashboard
                                    </button>
                                </div>
                            </div>
                        </div>
                    </>
                )}

                {showLogin && (
                    <>
                        <h1 style={{ fontSize: '1.875rem', lineHeight: '2.25rem', fontWeight: 700, textAlign: 'center', marginBottom: '2rem' }}>
                            Sign in
                        </h1>
                        <div style={cardStyle}>
                            <div style={{ padding: '1.5rem' }}>
                                <form onSubmit={handleLogin} style={formGridStyle}>
                                    <input
                                        type="text"
                                        placeholder="Username"
                                        value={loginForm.username}
                                        onChange={(e) => setLoginForm({ ...loginForm, username: e.target.value })}
                                        style={inputStyle}
                                        required
                                    />
                                    <input
                                        type="password"
                                        placeholder="Password"
                                        value={loginForm.password}
                                        onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
                                        style={inputStyle}
                                        required
                                    />
                                    {authError && <div style={{ color: '#b91c1c' }}>{authError}</div>}
                                    <button type="submit" style={submitButtonStyle}>
                                        Sign in
                                    </button>
                                    <button type="button" style={secondaryButtonStyle} onClick={() => navigate('/')}>
                                        <Home size={16} />
                                        Back to home
                                    </button>
                                </form>
                            </div>
                        </div>
                    </>
                )}

                {showDashboard && authToken && (
                    <>
                        <h1 style={{ fontSize: '1.875rem', lineHeight: '2.25rem', fontWeight: 700, textAlign: 'center', marginBottom: '2rem' }}>
                            Pharma Blockchain MVP
                        </h1>
                        <div style={cardStyle}>
                            <div style={tabContainerStyle}>
                                <button
                                    style={{ ...tabButtonStyle, ...(activeTab === 'add' ? activeTabButtonStyle : inactiveTabButtonStyle) }}
                                    onClick={() => setActiveTab('add')}
                                >
                                    <Plus style={{ display: 'inline-block', marginRight: '0.5rem' }} size={16} />
                                    Add Medication
                                </button>
                                <button
                                    style={{ ...tabButtonStyle, ...(activeTab === 'view' ? activeTabButtonStyle : inactiveTabButtonStyle) }}
                                    onClick={() => setActiveTab('view')}
                                >
                                    <List style={{ display: 'inline-block', marginRight: '0.5rem' }} size={16} />
                                    View All
                                </button>
                            </div>
                            <div style={{ padding: '1.5rem' }}>
                                {statusMessage && <div style={{ color: '#2563eb', marginBottom: '1rem' }}>{statusMessage}</div>}
                                {activeTab === 'add' && (
                                    <form onSubmit={handleSubmit}>
                                        <div style={formGridStyle}>
                                            <input
                                                type="text"
                                                name="serialNumber"
                                                placeholder="Serial Number (UID)"
                                                value={formData.serialNumber}
                                                onChange={handleInputChange}
                                                style={inputStyle}
                                                required
                                            />
                                            <input
                                                type="text"
                                                name="medicationName"
                                                placeholder="Medication Name"
                                                value={formData.medicationName}
                                                onChange={handleInputChange}
                                                style={inputStyle}
                                                required
                                            />
                                            <input
                                                type="text"
                                                name="gtin"
                                                placeholder="GTIN"
                                                value={formData.gtin}
                                                onChange={handleInputChange}
                                                style={inputStyle}
                                                required
                                            />
                                            <input
                                                type="text"
                                                name="batchNumber"
                                                placeholder="Batch Number"
                                                value={formData.batchNumber}
                                                onChange={handleInputChange}
                                                style={inputStyle}
                                                required
                                            />
                                            <input
                                                type="date"
                                                name="expiryDate"
                                                placeholder="Expiry Date"
                                                value={formData.expiryDate}
                                                onChange={handleInputChange}
                                                style={inputStyle}
                                                required
                                            />
                                            <input
                                                type="text"
                                                name="productionCompany"
                                                placeholder="Production Company"
                                                value={formData.productionCompany}
                                                onChange={handleInputChange}
                                                style={inputStyle}
                                                required
                                            />
                                            <input
                                                type="text"
                                                name="distributionCompany"
                                                placeholder="Distribution Company"
                                                value={formData.distributionCompany}
                                                onChange={handleInputChange}
                                                style={inputStyle}
                                                required
                                            />
                                        </div>
                                        <button type="submit" style={submitButtonStyle}>
                                            Add to Blockchain
                                        </button>
                                    </form>
                                )}
                                {activeTab === 'view' && (
                                    <div>
                                        <button onClick={fetchMedications} style={refreshButtonStyle}>
                                            <RefreshCw size={16} />
                                            Refresh
                                        </button>
                                        <div style={tableContainerStyle}>
                                            <table style={tableStyle}>
                                                <thead style={tableHeaderStyle}>
                                                    <tr>
                                                        <th style={thStyle}>Serial</th>
                                                        <th style={thStyle}>Name</th>
                                                        <th style={thStyle}>GTIN</th>
                                                        <th style={thStyle}>Batch</th>
                                                        <th style={thStyle}>Expiry</th>
                                                        <th style={thStyle}>Production</th>
                                                        <th style={thStyle}>Distribution</th>
                                                        <th style={thStyle}>QR</th>
                                                    </tr>
                                                </thead>
                                                <tbody style={{ borderBottom: '1px solid #e5e7eb' }}>
                                                    {medications.map((med) => (
                                                        <tr key={med.serialNumber} style={{ borderBottom: '1px solid #e5e7eb' }}>
                                                            <td style={tdStyle}>{med.serialNumber}</td>
                                                            <td style={tdStyle}>{med.medicationName}</td>
                                                            <td style={tdStyle}>{med.gtin}</td>
                                                            <td style={tdStyle}>{med.batchNumber}</td>
                                                            <td style={tdStyle}>{med.expiryDate}</td>
                                                            <td style={tdStyle}>{med.productionCompany}</td>
                                                            <td style={tdStyle}>{med.distributionCompany}</td>
                                                            <td style={tdStyle}>
                                                                {med.qrHash ? (
                                                                    <button onClick={() => handleShowQR(med.qrHash || '')} style={secondaryButtonStyle}>
                                                                        Show QR
                                                                    </button>
                                                                ) : (
                                                                    'â€”'
                                                                )}
                                                            </td>
                                                        </tr>
                                                    ))}
                                                </tbody>
                                            </table>
                                        </div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </>
                )}
            </div>

            {showQRModal && (
                <div className="modal" onClick={() => setShowQRModal(false)}>
                    <div className="modal-card" onClick={(e) => e.stopPropagation()}>
                        <QRCodeSVG value={selectedQRHash} size={256} />
                        <p>{selectedQRHash}</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default App;
