import React, { useEffect, useMemo, useState } from 'react';
import {
    Plus,
    List,
    RefreshCw,
    Search,
    QrCode,
    Copy,
    Check,
    ShieldCheck,
    Loader2,
    LogIn,
    Home
} from 'lucide-react';
import { QRCodeSVG } from 'qrcode.react';
import './App.css';

type Toast = { type: 'success' | 'error' | 'info'; message: string };

const API_BASE =
    (process.env.REACT_APP_API_BASE_URL && process.env.REACT_APP_API_BASE_URL.trim()) ||
    (process.env.NEXT_PUBLIC_API_BASE_URL && process.env.NEXT_PUBLIC_API_BASE_URL.trim()) ||
    (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1'
        ? 'http://localhost:3001'
        : 'https://ledgrx.duckdns.org');

const buildUrl = (path: string) => `${API_BASE.replace(/\/$/, '')}${path}`;

type Medication = {
    serialNumber: string;
    medicationName: string;
    gtin: string;
    batchNumber: string;
    expiryDate: string;
    productionCompany: string;
    distributionCompany: string;
    qrHash?: string;
};

const App: React.FC = () => {
    const [route, setRoute] = useState(window.location.pathname || '/');
    const [activeTab, setActiveTab] = useState<'add' | 'view'>('add');
    const [medications, setMedications] = useState<Medication[]>([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [error, setError] = useState('');
    const [addError, setAddError] = useState('');
    const [toast, setToast] = useState<Toast | null>(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [lastUpdated, setLastUpdated] = useState<string | null>(null);
    const [lookupSerial, setLookupSerial] = useState('');
    const [lookupResult, setLookupResult] = useState<Medication | null>(null);
    const [lookupError, setLookupError] = useState('');
    const [lookupLoading, setLookupLoading] = useState(false);
    const [formData, setFormData] = useState<Medication>({
        serialNumber: '',
        medicationName: '',
        gtin: '',
        batchNumber: '',
        expiryDate: '',
        productionCompany: '',
        distributionCompany: ''
    });
    const [showQRModal, setShowQRModal] = useState(false);
    const [selectedQRHash, setSelectedQRHash] = useState('');
    const [copied, setCopied] = useState(false);
    const [authToken, setAuthToken] = useState<string | null>(null);
    const [authError, setAuthError] = useState('');
    const [loginForm, setLoginForm] = useState({ username: '', password: '' });
    const [authMode, setAuthMode] = useState<'login' | 'signup'>('login');

    useEffect(() => {
        const stored = localStorage.getItem('authToken');
        if (stored) {
            setAuthToken(stored);
        }
    }, []);

    useEffect(() => {
        const onPopState = () => setRoute(window.location.pathname || '/');
        window.addEventListener('popstate', onPopState);
        return () => window.removeEventListener('popstate', onPopState);
    }, []);

    const navigate = (path: string, mode?: 'login' | 'signup') => {
        if (path === route) return;
        window.history.pushState({}, '', path);
        setRoute(path);
        if (mode) {
            setAuthMode(mode);
        }
    };

    useEffect(() => {
        if (!toast) return;
        const timer = setTimeout(() => setToast(null), 3200);
        return () => clearTimeout(timer);
    }, [toast]);

    const authFetch = (path: string, options: RequestInit = {}) => {
        const headers = new Headers(options.headers || {});
        if (authToken) {
            headers.set('Authorization', `Bearer ${authToken}`);
        }
        if (!headers.has('Content-Type') && options.body) {
            headers.set('Content-Type', 'application/json');
        }
        return fetch(buildUrl(path), { ...options, headers });
    };

    const fetchMedications = async () => {
        setIsLoading(true);
        setError('');
        try {
            const response = await authFetch('/api/medications');
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                throw new Error(errorData.error || 'Failed to fetch medications.');
            }
            const data = await response.json();
            setMedications(data);
            setLastUpdated(new Date().toLocaleString());
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setError(message || 'Unable to reach the API.');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        if (route === '/app' && activeTab === 'view') {
            fetchMedications();
        }
    }, [route, activeTab]);

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setAddError('');
        if (
            !formData.serialNumber ||
            !formData.medicationName ||
            !formData.gtin ||
            !formData.batchNumber ||
            !formData.expiryDate ||
            !formData.productionCompany ||
            !formData.distributionCompany
        ) {
            setAddError('All fields are required.');
            return;
        }
        setIsSubmitting(true);
        try {
            const response = await authFetch('/api/medications', {
                method: 'POST',
                body: JSON.stringify(formData)
            });
            if (response.ok) {
                const data = await response.json().catch(() => ({}));
                setToast({ type: 'success', message: data.qrHash ? 'Medication anchored on-chain.' : 'Medication added.' });
                setFormData({
                    serialNumber: '',
                    medicationName: '',
                    gtin: '',
                    batchNumber: '',
                    expiryDate: '',
                    productionCompany: '',
                    distributionCompany: ''
                });
            } else {
                const errorData = await response.json();
                const message = errorData.error || 'Upload failed.';
                setAddError(message);
                setToast({ type: 'error', message });
            }
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setAddError(message || 'An error occurred while adding the medication.');
            setToast({ type: 'error', message: message || 'Upload failed.' });
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleShowQR = (qrHash: string) => {
        if (showQRModal && selectedQRHash === qrHash) {
            setShowQRModal(false);
            return;
        }
        setSelectedQRHash(qrHash);
        setShowQRModal(true);
    };

    const handleCopyHash = async () => {
        if (!selectedQRHash) return;
        try {
            await navigator.clipboard.writeText(selectedQRHash);
            setCopied(true);
            setTimeout(() => setCopied(false), 2000);
        } catch (error) {
            setToast({ type: 'error', message: 'Copy failed. Try manually selecting the hash.' });
        }
    };

    const handleLookup = async () => {
        if (!lookupSerial.trim()) {
            setLookupError('Enter a serial number to search.');
            return;
        }
        setLookupLoading(true);
        setLookupError('');
        setLookupResult(null);
        try {
            const response = await authFetch(`/api/medications/${encodeURIComponent(lookupSerial.trim())}`);
            if (response.status === 404) {
                const errorData = await response.json().catch(() => ({}));
                setLookupError(errorData.error || 'Medication not found.');
                return;
            }
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                throw new Error(errorData.error || 'Lookup failed.');
            }
            const data = await response.json();
            setLookupResult(data);
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setLookupError(message || 'Lookup failed.');
        } finally {
            setLookupLoading(false);
        }
    };

    const handleHealthCheck = async () => {
        try {
            const response = await fetch(buildUrl('/api/health'));
            if (!response.ok) {
                throw new Error('Backend not reachable.');
            }
            setToast({ type: 'success', message: 'Backend online' });
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setToast({ type: 'error', message: message || 'Backend offline' });
        }
    };

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setAuthError('');
        if (!loginForm.username || !loginForm.password) {
            setAuthError('Enter your username and password.');
            return;
        }
        try {
            const response = await fetch(buildUrl('/api/auth/login'), {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginForm)
            });
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                setAuthError(errorData.error || 'Login failed.');
                return;
            }
            const data = await response.json();
            if (!data.token) {
                setAuthError('Login failed.');
                return;
            }
            localStorage.setItem('authToken', data.token);
            setAuthToken(data.token);
            setLoginForm({ username: '', password: '' });
            navigate('/app');
        } catch (error: unknown) {
            const message = error instanceof Error ? error.message : String(error);
            setAuthError(message || `Login failed. Check API at ${API_BASE}.`);
        }
    };

    const filteredMedications = useMemo(() => {
        if (!searchQuery.trim()) return medications;
        const q = searchQuery.trim().toLowerCase();
        return medications.filter((med) => {
            const fields = [
                med.serialNumber,
                med.medicationName,
                med.gtin,
                med.batchNumber,
                med.expiryDate,
                med.productionCompany,
                med.distributionCompany,
                med.qrHash
            ].map((value) => value ?? '');
            return fields.some((value) => value.toLowerCase().includes(q));
        });
    }, [medications, searchQuery]);

    const showDashboard = route === '/app';
    const showLogin = route === '/login' || (!authToken && route === '/app');
    const showHome = !showDashboard && !showLogin;

    return (
        <div className="app">
            <div className="app__glow app__glow--one" />
            <div className="app__glow app__glow--two" />

            <header className="topbar">
                <button className="brand" onClick={() => navigate('/')}>
                    LedgerRx
                </button>
                <nav className="topbar__nav">
                    <button className="topbar__link" onClick={() => navigate('/')}>
                        <Home size={16} />
                        Home
                    </button>
                    <button className="topbar__link" onClick={() => navigate('/app')}>
                        <ShieldCheck size={16} />
                        Dashboard
                    </button>
                    <button className="topbar__link" onClick={() => navigate('/login')}>
                        <LogIn size={16} />
                        Sign in
                    </button>
                </nav>
            </header>

            {showHome && (
                <header className="hero">
                    <div className="hero__badge">
                        <ShieldCheck size={16} />
                        Trusted pharma traceability
                    </div>
                    <h1>LedgerRx Control Center</h1>
                    <p>
                        Anchor medication lineage to your private Fabric network. Track batches, verify origin,
                        and generate QR codes for downstream scanning.
                    </p>
                    <div className="hero__stats">
                        <div>
                            <span>Total Records</span>
                            <strong>{medications.length}</strong>
                        </div>
                        <div>
                            <span>Network Status</span>
                            <strong>{error ? 'Offline' : 'Online'}</strong>
                        </div>
                        <div>
                            <span>Last Sync</span>
                            <strong>{lastUpdated ?? '—'}</strong>
                        </div>
                    </div>
                    <div className="hero__actions">
                        <button className="button button--primary" onClick={() => navigate('/login')}>
                            <LogIn size={16} />
                            Sign in
                        </button>
                        <button className="button button--ghost" onClick={() => navigate('/app')}>
                            <ShieldCheck size={16} />
                            Open dashboard
                        </button>
                    </div>
                </header>
            )}

            {showLogin && (
                <section className="panel panel--center">
                    <form className="card card--form auth-card" onSubmit={handleLogin}>
                        <h2>Welcome back</h2>
                        <p>Sign in to continue managing medication records.</p>
                        <div className="field">
                            <label>Username</label>
                            <input
                                type="text"
                                value={loginForm.username}
                                onChange={(e) => setLoginForm({ ...loginForm, username: e.target.value })}
                                required
                            />
                        </div>
                        <div className="field">
                            <label>Password</label>
                            <input
                                type="password"
                                value={loginForm.password}
                                onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
                                required
                            />
                        </div>
                        {authError && <div className="inline-error">{authError}</div>}
                        <div className="form__actions">
                            <button type="submit" className="button button--primary">
                                Sign in
                            </button>
                            <button type="button" className="button button--ghost" onClick={() => navigate('/')}>
                                <Home size={16} />
                                Back to home
                            </button>
                        </div>
                    </form>
                </section>
            )}

            {showDashboard && authToken && (
                <>
                    <header className="hero">
                        <div className="hero__badge">
                            <ShieldCheck size={16} />
                            Trusted pharma traceability
                        </div>
                        <h1>LedgerRx Control Center</h1>
                        <p>
                            Anchor medication lineage to your private Fabric network. Track batches, verify origin,
                            and generate QR codes for downstream scanning.
                        </p>
                        <div className="hero__stats">
                            <div>
                                <span>Total Records</span>
                                <strong>{medications.length}</strong>
                            </div>
                            <div>
                                <span>Network Status</span>
                                <strong>{error ? 'Offline' : 'Online'}</strong>
                            </div>
                            <div>
                                <span>Last Sync</span>
                                <strong>{lastUpdated ?? '—'}</strong>
                            </div>
                        </div>
                    </header>

                    <section className="panel">
                        <div className="panel__tabs">
                            <button
                                className={activeTab === 'add' ? 'tab tab--active' : 'tab'}
                                onClick={() => setActiveTab('add')}
                            >
                                <Plus size={16} />
                                Add Medication
                            </button>
                            <button
                                className={activeTab === 'view' ? 'tab tab--active' : 'tab'}
                                onClick={() => setActiveTab('view')}
                            >
                                <List size={16} />
                                View Records
                            </button>
                        </div>

                        <div className="panel__body">
                            {activeTab === 'add' && (
                                <div className="grid">
                                    <form className="card card--form" onSubmit={handleSubmit}>
                                        <h2>New medication entry</h2>
                                        <p>All fields are required to generate a traceable QR payload.</p>

                                        <div className="field">
                                            <label>Serial Number (UID)</label>
                                            <input
                                                type="text"
                                                name="serialNumber"
                                                placeholder="RX-2026-00001"
                                                value={formData.serialNumber}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="field">
                                            <label>Medication Name</label>
                                            <input
                                                type="text"
                                                name="medicationName"
                                                placeholder="Aspirin"
                                                value={formData.medicationName}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="field">
                                            <label>GTIN</label>
                                            <input
                                                type="text"
                                                name="gtin"
                                                placeholder="00312345678905"
                                                value={formData.gtin}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="field">
                                            <label>Batch Number</label>
                                            <input
                                                type="text"
                                                name="batchNumber"
                                                placeholder="BATCH-APR-26"
                                                value={formData.batchNumber}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="field">
                                            <label>Expiry Date</label>
                                            <input
                                                type="date"
                                                name="expiryDate"
                                                value={formData.expiryDate}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="field">
                                            <label>Production Company</label>
                                            <input
                                                type="text"
                                                name="productionCompany"
                                                placeholder="PharmaCorp"
                                                value={formData.productionCompany}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="field">
                                            <label>Distribution Company</label>
                                            <input
                                                type="text"
                                                name="distributionCompany"
                                                placeholder="Global Logistics"
                                                value={formData.distributionCompany}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>

                                        <div className="form__actions">
                                            <button type="submit" className="button button--primary" disabled={isSubmitting}>
                                                {isSubmitting ? <Loader2 size={16} className="spin" /> : <QrCode size={16} />}
                                                {isSubmitting ? 'Anchoring...' : 'Add to Blockchain'}
                                            </button>
                                        </div>
                                        {addError && <div className="inline-error">{addError}</div>}
                                    </form>

                                    <div className="card card--preview">
                                        <div className="preview__header">
                                            <div>
                                                <h3>QR readiness</h3>
                                                <p>QR hash is generated after the transaction is committed.</p>
                                            </div>
                                            <div className="preview__status">Ready</div>
                                        </div>
                                        <div className="preview__body">
                                            <div className="preview__placeholder">
                                                <QrCode size={32} />
                                                <span>Submit to mint a QR hash</span>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {activeTab === 'view' && (
                                <div className="records">
                                    <div className="records__toolbar">
                                        <div className="search">
                                            <Search size={16} />
                                            <input
                                                type="text"
                                                placeholder="Search serial, name, batch..."
                                                value={searchQuery}
                                                onChange={(e) => setSearchQuery(e.target.value)}
                                            />
                                        </div>
                                        <button className="button button--ghost" onClick={fetchMedications}>
                                            <RefreshCw size={16} className={isLoading ? 'spin' : ''} />
                                            {isLoading ? 'Refreshing' : 'Refresh'}
                                        </button>
                                    </div>

                                    {error && <div className="error-banner">{error}</div>}

                                    <div className="records__grid">
                                        {filteredMedications.length === 0 && !isLoading && (
                                            <div className="empty-state">
                                                <QrCode size={32} />
                                                <p>No records yet. Add a medication to mint the first QR hash.</p>
                                            </div>
                                        )}
                                        {filteredMedications.map((med) => (
                                            <div className="record-card" key={med.serialNumber}>
                                                <div className="record-card__header">
                                                    <div>
                                                        <span className="pill">Serial</span>
                                                        <h3>{med.serialNumber}</h3>
                                                    </div>
                                                    <button className="button button--mini" onClick={() => handleShowQR(med.qrHash || '')}>
                                                        <QrCode size={14} />
                                                        View QR
                                                    </button>
                                                </div>
                                                <div className="record-card__meta">
                                                    <div>
                                                        <span>Name</span>
                                                        <strong>{med.medicationName}</strong>
                                                    </div>
                                                    <div>
                                                        <span>GTIN</span>
                                                        <strong>{med.gtin}</strong>
                                                    </div>
                                                    <div>
                                                        <span>Batch</span>
                                                        <strong>{med.batchNumber}</strong>
                                                    </div>
                                                    <div>
                                                        <span>Expiry</span>
                                                        <strong>{med.expiryDate}</strong>
                                                    </div>
                                                </div>
                                                <div className="record-card__hash">
                                                    <span>QR Hash</span>
                                                    <button className="button button--ghost button--mini" onClick={() => handleShowQR(med.qrHash || '')}>
                                                        Show
                                                    </button>
                                                </div>
                                            </div>
                                        ))}
                                    </div>

                                    <div className="lookup card">
                                        <h3>Scan / lookup</h3>
                                        <p>Enter a serial number from a QR scan to fetch on-chain data.</p>
                                        <div className="lookup__row">
                                            <input
                                                type="text"
                                                placeholder="Serial number"
                                                value={lookupSerial}
                                                onChange={(e) => setLookupSerial(e.target.value)}
                                            />
                                            <button className="button button--primary" onClick={handleLookup} disabled={lookupLoading}>
                                                {lookupLoading ? 'Searching...' : 'Search'}
                                            </button>
                                        </div>
                                        {lookupError && <div className="inline-error">{lookupError}</div>}
                                        {lookupResult && (
                                            <div className="lookup__result">
                                                <div>
                                                    <span>Serial</span>
                                                    <strong>{lookupResult.serialNumber}</strong>
                                                </div>
                                                <div>
                                                    <span>Name</span>
                                                    <strong>{lookupResult.medicationName}</strong>
                                                </div>
                                                <div>
                                                    <span>GTIN</span>
                                                    <strong>{lookupResult.gtin}</strong>
                                                </div>
                                                <div>
                                                    <span>Batch</span>
                                                    <strong>{lookupResult.batchNumber}</strong>
                                                </div>
                                                <div>
                                                    <span>Production</span>
                                                    <strong>{lookupResult.productionCompany}</strong>
                                                </div>
                                                <div>
                                                    <span>Distribution</span>
                                                    <strong>{lookupResult.distributionCompany}</strong>
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            )}
                        </div>
                    </section>
                </>
            )}

            {toast && <div className={`toast toast--${toast.type}`}>{toast.message}</div>}

            {showQRModal && (
                <div className="modal" onClick={() => setShowQRModal(false)}>
                    <div className="modal__card" onClick={(e) => e.stopPropagation()}>
                        <div className="modal__header">
                            <div>
                                <h3>QR Payload</h3>
                                <p>Scan-ready hash for supply chain validation.</p>
                            </div>
                            <button className="button button--ghost button--mini" onClick={handleCopyHash}>
                                {copied ? <Check size={14} /> : <Copy size={14} />}
                                {copied ? 'Copied' : 'Copy hash'}
                            </button>
                        </div>
                        <div className="modal__body">
                            <QRCodeSVG value={selectedQRHash} size={220} />
                            <p className="hash">{selectedQRHash}</p>
                        </div>
                    </div>
                </div>
            )}

            <footer className="footer">
                <span>LedgerRx • Private pharma traceability</span>
                <span>Powered by Hyperledger Fabric</span>
            </footer>
        </div>
    );
};

export default App;
